import React from "react"
import raw from "./terms.txt"

class Flashcards extends React.Component{
    constructor(){
        super();
        this.state={
            termIndex: 0,
            definition: "definition",
            onTerm: true,
            allTerms: []
        }
    }

    componentDidMount() {
        //import terms txt file
        fetch(raw)
            .then(response => response.text())
            .then(text => {
                const terms = text.split("\n");
                this.setState({allTerms: terms})
            })
            .then(() => {
                //shuffle terms
                const shuffledTerms = this.shuffleTerms(this.state.allTerms);

                //shuffle allTerms
                this.setState({allTerms: shuffledTerms})
                console.log(this.state.allTerms)
            })
    }

    shuffleTerms(shuffledTerms) {
        const totalTerms = shuffledTerms.length

        for (var i=0; i<totalTerms; i++){
            const random = i + Math.floor(Math.random() * (totalTerms-i));
            const indexTerm = shuffledTerms[i];
            shuffledTerms[i] = shuffledTerms[random];
            shuffledTerms[random] = indexTerm;
        }

        return shuffledTerms;
    }

    lastTerm = () => {
        if (this.state.termIndex > 0){
            this.setState({termIndex: this.state.termIndex - 1})
        }
    }

    nextTerm = () => {
        if (this.state.termIndex < this.state.allTerms.length - 1){
            this.setState({termIndex: this.state.termIndex + 1})
        }
    }

    handleClick = () => {
        this.setState(prevState => {
            return {
                onTerm: !prevState.onTerm
            }
        })
    }

    render() {
        const flashcardText = this.state.onTerm ? 
            this.state.allTerms[this.state.termIndex] : 
            this.state.definition

        return (
            <div className="Main">
                <label className="Flashcard" >
                    <button 
                        onClick={this.handleClick}
                    >{flashcardText}</button>
                </label>

                <label>
                    <button
                        className=""
                        onClick={this.lastTerm}
                    >Back</button>
                </label>

                <label>
                    <button
                        className=""
                        onClick={this.nextTerm}
                    >Next</button>
                </label>
            </div>
        )
    }
}

export default Flashcards;
