import React from "react"

class Flashcards extends React.Component{
    constructor(){
        super();
        this.state={
            term: "",
            definition: "",
            onTerm: true,
            allTerms: ""
        }
    }

    hnadleClick = () => {
        this.setState(prevState =>  {
            return({onTerm: !prevState.onTerm})})
    }

    render() {
        const flashcardText = this.state.onTerm ? 
            this.state.term : 
            this.state.definition

        return (
            <div className="Main">
                <button
                    className="Flashcard"
                >{flashcardText}</button>
            </div>
        )
    }
}

export default Flashcards;
