class Flashcard {
    constructor(term) {
        this.term = term
        this.definition = this.fetchDefinition(term)
    }

    fetchDefinition(term) {
        const key = "cb25488e-3745-487a-8e94-4f5892774491"
        const url = `https://www.dictionaryapi.com/api/v3/references/collegiate/json/${term}?key=${key}`

        fetch(url)
            .then(response => {
                if (!response.ok) {
                    throw response;
                }
                return response.json()
            })
            .then(data => {
                const termDefinition = this.parseData(data)
                this.setState({ definition: termDefinition })
            })
            .catch(err => {
                this.setState({ definition: "Error: Not a Real English Word!" })
            })
    }

    get Term() {
        return this.term;
    }

    get Definition() {
        return this.definition
    }
}

export default Flashcard;
