    await fetch(url)
        .then(response => {
            if (!response.ok) {
                throw response;
            }
            return response.json()
        })
        .then(data => {
            result = data
        })
        .catch(err => {
            this.setState({definition: "Error: No such word!"})
        })
}

function parseData(data) {
    let definitionText = ``
    let totalCounter = 1

    for(let i=0; i<data.length;i++){
        const shortDefArray = data[i].shortdef

        for(let j=0; j<shortDefArray.length;j++){
            definitionText += `${String(totalCounter)}. ${shortDefArray[j]}\n`
            totalCounter++;
        }
    }

    console.log(typeof(definitionText))

    return definitionText
}



class Flashcard {

    constructor(term) {
        this.term = term
        this.definition = fetchDefinition(term)
    }

    get Term() {
        return this.term;
    }

    get Definition() {
        return this.definition
    }

}

export default Flashcard;
